INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (19, 1, 1, 'Poison', 'Poison damage will give you dangerous diseases that eventually may kill you slowly and painfully. This attribute both functions for as damage type for weapon and protection. The maximum value is related to the maximum amount of protection a character can have in this attribute.', NULL, 5, 0, 0, 100, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (20, 1, 1, 'Slashing', 'Slashing damage is the kind you suffer from sharp edges, such as swords. This attribute both functions for as damage type for weapon and protection. The maximum value is related to the maximum amount of protection a character can have in this attribute.', 'http://cdn.spelwerk.com/file/682b10bc4a0432fc48b49478611931212fcf39cb.png', 5, 0, 0, 100, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (21, 1, 1, 'Honor', 'An individual\\\'s honor is a measure of her social status amongst the law-abiding citizens of the world. The honor bonus is added to all social rolls where this may affect the outcome. It may also be used as a detriment to her rolls if the individual dislikes honourable people.', 'http://cdn.spelwerk.com/file/4b7da4b5a6f63a5da62a7fb503429981de57de43.png', 6, 0, 0, 50, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (22, 1, 1, 'Notoriety', 'An individual\\\'s notoriety is a measure of  her social status amongst the unlawful people of the world. The notoriety bonus is added to all social rolls where this may affect the outcome. It may also be used as a detriment to her rolls if the individual dislikes notorious people.', 'http://cdn.spelwerk.com/file/911ff09e642dc930aab0b62d9f5340580469493a.png', 6, 0, 0, 50, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (23, 1, 1, 'Ammunition', 'Ammunition is a comprehensive measure of an individual\\\'s probability of having the required ammunition going into combat.', 'http://cdn.spelwerk.com/file/f3740c896528e0115a770bdeb785d019d4f42ef8.png', 7, 0, 1, 100, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (24, 1, 1, 'Rations', 'Rations is a comprehensive measure of an individual\\\'s amount of provisions when going where there\\\'s no easy access to food. Ration rolls will be made on a daily basis.', 'http://cdn.spelwerk.com/file/c14931e96c11091486915dc974a117e54402fc69.png', 7, 0, 1, 100, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (25, 1, 1, 'Wealth', 'Wealth is a comprehensive measure of an individual\\\'s prosperity, affluence, money, assets. Any item purchased is measured against this value and if it is equal to or higher than the individual\\\'s wealth a dice check must be rolled.', 'http://cdn.spelwerk.com/file/aedeb0c54cb52f9b49b6a878388d1c25a01fdcbe.png', 7, 0, 1, 1000, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (26, 1, 1, 'Experience', 'Experience is an individual\\\'s reward for completing quests, being in character, and accomplishing astonishing feats. It can be used to purchase, Attributes, Skills, Expertises, and Doctrines.', 'http://cdn.spelwerk.com/file/70b722c98ebd5ed02c06fe48d6f76225e61d38ae.png', 8, 0, 0, 1000, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (27, 1, 1, 'Energy', 'Energy is used in conjunction with Bionics and Augmentations. It measures the energy/battery/power that is used and is available to use before the individual has to add more power sources either through other bionics or new augmentations.', 'http://cdn.spelwerk.com/file/9e6858d1e55b47479f1c597bf40ef76cd0d4f4f9.png', 9, 0, 0, 1000, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`attribute` (`id`, `account_id`, `canon`, `name`, `description`, `icon`, `attribute_type_id`, `is_optional`, `minimum`, `maximum`, `created`, `updated`, `deleted`) VALUES (28, 1, 1, 'Potential', 'Power Attribute for Manifestation: Empath', 'http://cdn.spelwerk.com/file/844d473a411e1abab5dd9d2c8631fe51b7f79803.png', 10, 1, 0, 1000, DEFAULT, DEFAULT, DEFAULT);

