INSERT INTO `saga_v1`.`milestone` (`id`, `account_id`, `canon`, `name`, `description`, `created`, `updated`, `deleted`) VALUES (22, 1, 1, 'Explorer', 'You spent a year exploring\\, and discovered a previously unknown tribe! You spent a year learning how to communicate with them.', DEFAULT, DEFAULT, DEFAULT);
