INSERT INTO `saga_v1`.`milestone` (`id`, `account_id`, `canon`, `name`, `description`, `created`, `updated`, `deleted`) VALUES (63, 1, 1, 'Partner', 'You were offered to become partner in a startup\\, and it was a wild success.', DEFAULT, DEFAULT, DEFAULT);
