INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (1, 1, 1, '9G Antenna', 'Antenna Module used together with the Internal Computer to connect to the Sense/Net wherever you are.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (2, 1, 1, 'A.T.L.A.S.&reg; Kevlar Protection', 'Small durable threads permeate the outer layer of the bionic  making it very resistant to ballistic damage.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (3, 1, 1, 'A.T.L.A.S.&reg; Plate Protection', 'Thick plates are installed on top of static areas on the bionic\\, making the user more durable against certain attacks.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (4, 1, 1, 'Air Filtration System', 'Air filtration is used primarily to enable the user to breathe in severely hostile environments.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (5, 1, 1, 'Alternate Finger Toolset', 'Toolset hidden in a finger to help lockpicking.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (6, 1, 1, 'Automated Flash Reduction', 'Automatically reduces the effectiveness of strong flashes.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (7, 1, 1, 'Automatic Defibrilator', 'Detects when the heart has stopped and starts the CPR process automatically to increase chances of survival\\, as well as giving a small chance of waking up immediately.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (8, 1, 1, 'Automatic Noise Reduction', 'Detects loud noises a nanosecond before they occur and lowers the intake volume in the ear so that the user does not suffer any permanent damage.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (9, 1, 1, 'Battery', 'Holds excess energy created\\, making it easier to have more augmentations connected to the bionic components.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (10, 1, 1, 'Blink Control Interface', 'Rudimentary switching of functions such as\\, Eye Colour\\, Night Vision\\, Scope Vision\\, Thermal Vision\\, X-Ray Vision.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (11, 1, 1, 'Blood Turbine System', 'A small turbine system is installed in the blood vessels\\, producing small amounts of energy\\, but is also very robust and cannot break down.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (12, 1, 1, 'Cerebral Control Interface', 'With this control interface\\, the user can control his/her computer with mere thoughts. Very exclusive. Very expensive. Only a small amount of surgeons can perform this without a high risk of death.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (13, 1, 1, 'Cold-Fusion Reactor', 'This augmentation can produce large amounts of energy\\, with virtually no radiation issues.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (14, 1, 1, 'Dynamic Iris Colour', 'The iris can change colour with either the use of the blink control interface\\, holographic arm interface\\, or the cerebral control interface.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (15, 1, 1, 'Efficient Breathing System', 'This augmentation can increase the efficiency of the lungs\\, which means the user has increased stamina.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (16, 1, 1, 'Electromagnet', 'By using a very powerful magnet\\, the user can push or pull metallic objects.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (17, 1, 1, 'EMP Shielding', 'Protects the bionic and its augmentations from EMP attacks. Very high tech. Very expensive.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (18, 1, 1, 'Enhanced Hearing System', 'Complex technology increasing the hearing distance of the ears.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (19, 1, 1, 'Flashlight', 'With a series of tiny light sources\\, a flashlight can be created to illuminate the surroundings a bit.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (20, 1, 1, 'Headphones', 'Headphones implanted to send sounds from the users computer\\, or comm-links.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (21, 1, 1, 'Hidden Pocket', 'Create a hidden pocket in the users bionic hardware\\, making smuggling very much easier.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (22, 1, 1, 'Holographic Interface', 'A holographic interface displaying information from the users computer.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (23, 1, 1, 'Holographic Keyboard', 'A holographic keyboard displayed on the users arm. Appears and disappears with a gesture\\, and is connected to your computer.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (24, 1, 1, 'HUD', 'Displays prudent information for the user in her field of view without disrupting too much.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (25, 1, 1, 'Hydraulic Enhancement', 'Adding complex small hydraulics to the limbs\\, making them sturdier and stronger.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (26, 1, 1, 'Internal Substance Injector', 'A secret refillable pouch will inject any given drug at will or automatically.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (27, 1, 1, 'Microphone', 'A small microphone records all sounds in the users vicinity.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (28, 1, 1, 'Motion Detector', 'Small motion detectors are installed\\, increasing overall awareness of movement in the users surroundings.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (29, 1, 1, 'NFC Chip', 'A Near Field Communicator chip is installed on the user\\, making it possible to pair up with other devices to the internal computer.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (30, 1, 1, 'Night Vision', 'Night Vision can be activated\\, making it easier to see at night.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (31, 1, 1, 'P.U.P.P.E.T.&trade; Back-Up Motor Functions System', 'Also known as the Zombie chip. A highly volatile (and outside of military - illegal) chip installed in the central nervous system. It works together with cables and components and can fully take over the body of a person when deceased. However there\'s nothing really saying the person cannot be controlled before death  which makes it very dangerous.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (32, 1, 1, 'P.U.P.P.E.T.&trade; Cut-Out', 'A chip mainly used temporarily over a year or two. The individual has a chip and electrodes installed in the cranium to enable the ability to \"\"switch off\"\" short-term memory storage for certain periods of time. They are also made quite malleable and open to almost all suggestions within the whitelist of limitations encoded on the ROM.\"\"', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (33, 1, 1, 'Projector', 'A small projector can be installed in the eye\\, which can display a small image on surfaces. Renders the eye unusable while active.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (34, 1, 1, 'Reflective Eyes', 'Reflects light back\\, making the eyes quite animalistic.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (35, 1, 1, 'Retractable Sunglasses', 'Installed over the eyes. Mostly because they are so damned cool to have.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (36, 1, 1, 'Scope', 'A series of small lenses can be installed inside the eye to give the user slightly better vision at long distances\\, increasing overall awareness.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (37, 1, 1, 'Sims Tim Sensory Chip', 'A sensory chip for Sims Tim Players is installed\\, which makes recording of emotions possible.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (38, 1, 1, 'Sims Tim Video Link', 'A video link is installed in the eye\\, making it possible to record whatever the user sees.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (39, 1, 1, 'T.R.U.&reg; Eye', 'A small but expensive modification making the eye extremely lifelike and only a trained surgeon will know that the eye is indeed bionic.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (40, 1, 1, 'T.R.U.&reg; Hair', 'Adding almost life-like hair to the bionic with dynamic length and colour.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (41, 1, 1, 'T.R.U.&reg; Skin', 'An expensive modification to the bionic using a lifelike layer of skin on top of the bionic components making the bionic virtually undetectable.', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (42, 1, 1, 'Thermal Vision', 'A thermal vision mode can be installed in the eye\\, giving the user the possibility to detect creatures easier.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (43, 1, 1, 'USB-G Port', 'A small port is installed on the bionic\\, giving the user a possibility to interface with virtually all devices in the world (now that USB-G is standard for everything)', 1, 0, 24, DEFAULT, DEFAULT, DEFAULT);
INSERT INTO `saga_v1`.`augmentation` (`id`, `account_id`, `canon`, `name`, `description`, `is_legal`, `price`, `hacking_difficulty`, `created`, `updated`, `deleted`) VALUES (44, 1, 1, 'Voice Distortion System', 'Small augmentations to the larynx making it possible for the user to distort her voice to sound more like another person\\, or beastlike.', 0, 0, 24, DEFAULT, DEFAULT, DEFAULT);
